CREATE TABLE PUBLIC.ADDRESS (id INTEGER AUTO_INCREMENT PRIMARY KEY NOT NULL, zip INTEGER, region VARCHAR(32) NOT NULL, district VARCHAR(32), city VARCHAR(32) NOT NULL, street VARCHAR(32) NOT NULL, house VARCHAR(8) NOT NULL, apartment VARCHAR(8), longitude DECIMAL(9, 6), latitude  DECIMAL(8, 6));
CREATE TABLE PUBLIC.CUSTOMER (id INTEGER AUTO_INCREMENT PRIMARY KEY NOT NULL, zip INTEGER, region VARCHAR(32) NOT NULL);